import React, { useState } from "react";
import { createAudience } from "../services/audienceService";
import "./styles/AudienceForm.css";
const AudienceForm = () => {
  const [name, setName] = useState("");
  const [criteria, setCriteria] = useState("");
  const [showPopup, setShowPopup] = useState(false);
  const [error, setError] = useState(""); // State for error messages

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setError(""); // Clear any previous error
      const parsedCriteria = JSON.parse(criteria); // Parse JSON string input from a text area
      const response = await createAudience(name, parsedCriteria);
      console.log("Audience Created:", response);
      setShowPopup(true); // Show success popup
      setTimeout(() => setShowPopup(false), 3000); // Hide popup after 3 seconds
      setName(""); // Reset form
      setCriteria("");
    } catch (error) {
      console.error("Error creating audience:", error.message);
      if (error instanceof SyntaxError) {
        setError("Invalid JSON format. Please correct it and try again.");
      } else {
        setError("Failed to create audience. Please try again.");
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Audience Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="criteria">Criteria (JSON):</label>
        <textarea
          id="criteria"
          value={criteria}
          onChange={(e) => setCriteria(e.target.value)}
        />
      </div>
      <button type="submit">Create Audience</button>
    </form>
  );
};

export default AudienceForm;
