import React, { createContext, useContext, useState, useEffect } from "react";
import { GoogleOAuthProvider, GoogleLogin } from "@react-oauth/google";
import { useNavigate } from "react-router-dom";

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Check localStorage for a logged-in user
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const handleLoginSuccess = (credentialResponse) => {
    if (user) return; // Prevent double execution

    try {
      const payload = JSON.parse(
        atob(credentialResponse.credential.split(".")[1])
      );
      const loggedInUser = {
        name: payload.name,
        email: payload.email,
        picture: payload.picture,
      };
      setUser(loggedInUser);
      localStorage.setItem("user", JSON.stringify(loggedInUser)); // Save user to localStorage
      navigate("/home");
    } catch (error) {
      console.error("Error decoding token:", error);
    }
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem("user"); // Clear user from localStorage
    navigate("/login");
  };

  return (
    <AuthContext.Provider value={{ user, handleLogout }}>
      <GoogleOAuthProvider clientId=" 29241022765-rv4rkrj4anslmuvk5lr2kfndu2lhjbj6.apps.googleusercontent.com">
        {!user ? (
          <div className="auth-container">
            <GoogleLogin
              onSuccess={handleLoginSuccess}
              onError={() => console.error("Login Failed")}
            />
          </div>
        ) : (
          children
        )}
      </GoogleOAuthProvider>
    </AuthContext.Provider>
  );
};

export default AuthContext;
