import React, { useState, useEffect } from "react";
import { fetchDeliveryReceipts } from "../services/communicationService";
import "./styles/DeliveryStatus.css";

const DeliveryStatus = () => {
  const [receipts, setReceipts] = useState([]);

  useEffect(() => {
    const loadReceipts = async () => {
      try {
        const data = await fetchDeliveryReceipts();
        setReceipts(data);
      } catch (error) {
        console.error("Failed to load delivery receipts:", error);
      }
    };

    loadReceipts();
  }, []);

  return (
    <div>
      <h1>Delivery Receipts</h1>
      <table>
        <thead>
          <tr>
            <th>Campaign Name</th>
            <th>Audience Size</th>
            <th>Customer Name</th>
            <th>Message</th>
            <th>Status</th>
            <th>Updated At</th>
          </tr>
        </thead>
        <tbody>
          {receipts.map((receipt) => (
            <tr key={receipt._id}>
              <td>{receipt.communication_log_id?.campaign_id?.name || "N/A"}</td>
              <td>{receipt.communication_log_id?.customer_id?.name || "N/A"}</td>
              <td>{receipt.communication_log_id?.message || "N/A"}</td>
              <td>{receipt.status}</td>
              <td>
                {receipt.updated_at
                  ? new Date(receipt.updated_at).toLocaleString()
                  : "N/A"}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DeliveryStatus;
