import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { AuthProvider } from "./context/AuthContext";
import HomePage from "./pages/HomePage";
import LoginPage from "./pages/LoginPage";
import AudiencePage from "./pages/AudiencePage";
import CampaignPage from "./pages/CampaignPage";
import StatsPage from "./components/StatsPage"; // Ensure this exists and is exported correctly

const App = () => {
  return (
    // Wrapping the entire app with the AuthProvider
    <AuthProvider>
      {/* Single Router wrapping all routes */}
      <Router>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/audiences" element={<AudiencePage />} />
          <Route path="/campaigns" element={<CampaignPage />} />
          <Route path="/stats" element={<StatsPage />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
};

export default App;
